mapper.select:
SELECT DISTINCT APP.* FROM MXK_APPS APP,MXK_GROUP_PRIVILEGES GP WHERE APP.ID=GP.APPID AND GP.GROUPID IN( SELECT G.ID FROM `MXK_GROUPS` G WHERE G.ID='ALL_USER_GROUP' OR G.ID IN( SELECT GM.GROUPID FROM MXK_GROUP_MEMBER GM,MXK_USERINFO U WHERE 1 = 1
AND U.ID = #{userId}
AND U.USERNAME = #{username}
AND GM.MEMBERID = U.ID ) )
AND NAME = #{name}
ORDER BY SORTINDEX
